CREATE TABLE promoted_channel (
  id BIGSERIAL NOT NULL,
  channel_description VARCHAR(255),
  channel_link VARCHAR(255),
  channel_name VARCHAR(255),
  chat_id INT8,
  PRIMARY KEY (id)
);

CREATE TABLE reminder_message (
  id BIGSERIAL NOT NULL,
  message VARCHAR(255),
  PRIMARY KEY (id)
);

CREATE TABLE user_data (
  chat_id INT8 NOT NULL,
  first_name VARCHAR(255),
  last_name VARCHAR(255),
  rating INT4,
  user_type VARCHAR(255),
  username VARCHAR(255),
  word_level VARCHAR(255),
  PRIMARY KEY (chat_id)
);

CREATE TABLE user_word_stats (
  user_id INT8 NOT NULL,
  word_id INT8 NOT NULL,
  correct_attempts INT4,
  incorrect_attempts INT4,
  last_attempt TIMESTAMP,
  PRIMARY KEY (user_id, word_id)
);

CREATE TABLE user_words (
  user_id INT8 NOT NULL,
  word_id INT8 NOT NULL
);

CREATE TABLE words (
  id BIGSERIAL NOT NULL,
  transcription VARCHAR(255),
  translation VARCHAR(255),
  word VARCHAR(255),
  word_level VARCHAR(255),
  word_type VARCHAR(255),
  PRIMARY KEY (id)
);

